$slickGreen = false;

function greenSlider() {
  if ($(window).width() <= 1090) {
    if (!$slickGreen) {
      $(".rate__row").slick({
        dots: false,
        arrows: true,
        slidesToShow: 1,
        slidesToScroll: 1,
      });
      $slickGreen = true;
    }
  } else if ($(window).width() > 1090) {
    if ($slickGreen) {
      $('.rate__row').slick('unslick');
      $slickGreen = false;
    }
  }
};

$slickGreen2 = false;

function greenSlider2() {
  if ($(window).width() <= 1280) {
    if (!$slickGreen2) {
      $(".workers__item-wrap").slick({
        dots: false,
        arrows: true,
        slidesToShow: 4,
        slidesToScroll: 1,
        responsive: [{
            breakpoint: 1000,
            settings: {
              slidesToShow: 3,
              slidesToScroll: 1,
            }
          },
          {
            breakpoint: 800,
            settings: {
              slidesToShow: 2,
              slidesToScroll: 1
            }
          },
          {
            breakpoint: 551,
            settings: {
              slidesToShow: 3,
              slidesToScroll: 1
            }
          },
          {
            breakpoint: 451,
            settings: {
              slidesToShow: 2,
              slidesToScroll: 1,
            }
          }
        ]
      });
      $slickGreen2 = true;
    }
  } else if ($(window).width() > 1280) {
    if ($slickGreen2) {
      $('.workers__item-wrap').slick('unslick');
      $slickGreen2 = false;
    }
  }
};

$(function () {
  $('.results-case').slick({
    slidesToShow: 1,
    speed: 400,
  });

  $('.menu__burger').on('click', function () {
    $('.menu__list').toggleClass('active');
    $(this).toggleClass('active');
    $('body').toggleClass('hidden');
  });

  greenSlider();
  greenSlider2();
});

$(window).on('resize', function () {
  greenSlider();
  greenSlider2();
});